#算全局效率
@not_implemented_for("directed")
def global_efficiency(G):
    n = len(G)
    denom = n * (n - 1)
    if denom != 0:
        lengths = nx.all_pairs_shortest_path_length(G)
        g_eff = 0
        for source, targets in lengths:
            for target, distance in targets.items():
                if distance > 0:
                    g_eff += 1 / distance
        g_eff /= denom
        # g_eff = sum(1 / d for s, tgts in lengths
        #                   for t, d in tgts.items() if d > 0) / denom
    else:
        g_eff = 0
    # TODO This can be made more efficient by computing all pairs shortest
    # path lengths in parallel.
    return g_eff


#随机攻击,times 是去除多少个点
def random_attack(G,times):
    graph = G.copy()
    steps = 1
    effs=[]
    print(graph.edges())
    effs.append(global_efficiency(G)/global_efficiency(G))
    #print("完整网络效率为 "+str(global_efficiency(G)));
    while times>0 and len(graph)>0:
        edge = random.choice(list(graph.edges()))
        #print(edge)
        i,j = edge
        graph.remove_edge(i,j)
        eff=global_efficiency(graph)
        effs.append(eff/global_efficiency(G))
        print("去除"+str(steps)+"个节点时效率为: "+str(eff))
        steps += 1
        times-=1


    #接下来开始画图
    x=[]
    for i in range(steps):
        #x.append(str(100*i/len(graph))+"%")
        x.append( i / len(G))
    plt.plot(x,effs,color='b')
    plt.gca().xaxis.set_major_locator(ticker.MultipleLocator(0.1))
    # 设置横轴的上下限
    plt.xlim(0, (steps/len(G)))
    plt.show()
    return graph



